INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (2667,'facebook','Diane Black (Representative from Tennessee)','As a parent and a former educator, I believe Tennessee students deserve every possible opportunity to succeed. That is why today I voted in support of the #QualityCharters Act which encourages school choice by promoting state efforts to develop and expand charter schools. Read more HERE: http://1.usa.gov/1hCxrPv','constituency',1,'neutral',1,'support',1,1);
