INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (2699,'facebook','Suzanne Bonamici (Representative from Oregon)','Today I introduced the SAFE Lending Act to crack down on online payday lenders. Oregon has some of the most aggressive anti-payday lender laws in the country, but online and offshore lenders are using a loophole to get around those laws. This bill will give the Consumer Financial Protection Bureau more authority to regulate these predatory activities.','national',1,'neutral',1,'other',1,1);
