INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (2860,'facebook','Henry Cuellar (Representative from Texas)','There are currently 19 House Republicans who have stated on the record that they would support a clean continuing resolution if it came to the Floor.  Combined with the 200 Democrats, these votes would reach the 218 votes needed for a majority.  We could end the shutdown that has put hundreds of thousands of Americans out of work and shuttered the federal government in 20 minutes if Speaker Boehner would allow a vote on the Floor of a clean Continuing Resolution.    #DemandAVote and end #GOPshutdown.    http://www.washingtonpost.com/blogs/the-fix/wp/2013/10/03/the-government-shutdown-could-end-today-all-it-would-cost-is-john-boehners-speakership/','national',1,'partisan',1,'policy',1,1);
