INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3124,'facebook','Ron DeSantis (Representative from Florida)','Today I joined Senator Ted Cruz, Senator Rand Paul, Congressman Steve Daines, Congressman Thomas Massie, and U.S. Representative John Fleming for a press conference in opposition to the internet sales tax. This legislation hurts Florida consumers and small businesses. Furthermore, if I don\'t trust the federal Internal Revenue Service, why on Earth would I want to subject Florida businesses to revenue collectors in California or Illinois? Read more here: http://bit.ly/13YPTvs. #NoNetTax','national',1,'partisan',1,'support',1,1);
