INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3132,'facebook','Tammy Duckworth (Representative from Illinois)','Today is Equal Pay Day. Equal Pay Day symbolizes when, more than three months into the year, women۪s wages finally catch up to what men were paid in the previous year. 51 years ago today Congress passed the Equal Pay Act  but women on average still earn only 77 cents for every dollar earned by men. I am a cosponsor of the Paycheck Fairness Act and urge my colleagues to work together to pass this crucial legislation.','national',1,'neutral',1,'policy',1,1);
