INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3224,'facebook','Bill Foster (Representative from Illinois)','I went to the White House to urge the Administration to support renewable energy by maintaining renewable fuel requirements. Our country must move away from its dependence on fossil fuels, toward a renewable energy economy, and ethanol is a critical component.   As a scientist and businessman, I believe that policy decisions should be based on the most accurate numbers, and recent analyses show that modern ethanol production is an important tool to preserve our environment and expand our economy.','national',1,'neutral',1,'policy',1,1);
