INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3324,'facebook','Robert Goodlatte (Representative from Virginia)','I welcome the news that Eric Holder will step down as Attorney General. From Operation Fast and Furious to his misleading testimony before the House Judiciary Committee regarding the Department۪s dealings with members of the media and his refusal to appoint a special counsel to investigate the IRS۪ targeting of conservative groups, Mr. Holder has consistently played partisan politics with many of the important issues facing the Justice Department. I hope that the next Attorney General will take seriously his role as the nation۪s top law enforcement officer, working with Congress to ensure that the laws of our land are followed instead of being a roadblock on the path to justice.','national',1,'partisan',1,'policy',1,1);
