INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3359,'facebook','Luis Gutierrez (Representative from Illinois)','Instead of continuing the blame-Obama game, Republicans should work to actually solve the crisis of children at our border. To do that, we need additional funds to follow the law, which says we must keep these kids safe while they are in our care and their individual cases must be processed by immigration courts quickly. We also need to work with the home countries in Central America so these kids can safely grow up in their home countries.','national',1,'partisan',1,'policy',1,1);
