INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3362,'facebook','Luis Gutierrez (Representative from Illinois)','Facebook, I need your help!  Each spring, every Congressperson selects one high schooler\'s art to represent their district in the Congressional Art Competition.  This year\'s theme for my district was \'The Importance of Family,\' because of the gun violence and deportations devastating our families. Here are my district\'s finalists.  Help me select which high schooler will go to D.C. and have their art displayed in the Capitol for an entire year by clicking \'Like\' on the art you like the most. Voting ends Thursday, May 2 at 2:00 PM CST.','national',1,'neutral',1,'support',1,1);
