INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3372,'facebook','Ra̼l Grijalva (Representative from Arizona)','A look behind the tea party curtain: \"Regardless of some PR experiments going on, the rank and file continue to laugh and cheer at old jokes and stump speeches. At the recent Freedom Forum \'cattle call,\' Mike Huckabee noted that sometimes he feels like North Korea is more free than the United States. Sen. Ted Cruz cried out about abolishing the Internal Revenue Service and was met with cheers.\"','national',1,'neutral',1,'support',1,1);
