INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3445,'facebook','Tulsi Gabbard (Representative from Hawaii)','Mahalo to the community leaders from Hawai\'i who stopped by to meet with Congresswoman Tulsi Gabbard today while they visited Washington, DC.  The State Representatives and Senators are in town for the National Conference of State Legislatures Fall Forum and spoke with Rep. Gabbard about ideas and concerns including transportation, healthcare, the Marketplace Fairness Act, and other important issues that affect the people of Hawai\'i.  Top photo: Sen. Maile Shimabukuro, Representative Scott Ishimoto, Tezcan Inanlar (Regional Director, Pacifica Institute), Sen. J. Kalani English, Congressman-elect Mark Takai, Sen. Clarence Nishihara, Corina Eckl (State Services Division Director, National Conference of State Legislatures), and Irene Kawanabe (Program Director, National Conference of State Legislatures).  Left photo: Trevor Takahashi, Volunteer Legislative Representative for the International Association of Machinists & Aerospace Workers.  Center photo: Sherry Menor-McNamara, President & CEO for the Chamber of Commerce Hawai\'i, with Enrico Fontana, PhD Managing Director for Ansaldo Honolulu.  Right photo:  Hawai\'i State Senator Brickwood Galuteria.','national',1,'neutral',1,'support',1,1);
