INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3535,'facebook','Bill Huizenga (Representative from Michigan)','Today I introduced legislation to enhance wildlife preservation, improve government efficiency, and protect taxpayer dollars.  Did you know know that litigation involving the the Endangered Species Act (ESA) cost taxpayers four times more than other government litigation cases?  This bill works to ensure that ESA resources are available for species protection and recovery instead of filling the pockets of lawyers.  Read more by clicking on the link below.','national',1,'neutral',1,'policy',1,1);
