INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3558,'facebook','John Hoeven (Senator from North Dakota)','The farm bill is one of the most important bills Congress passes, with the need to balance the interests of America۪s tremendous agricultural industry. Our bill will ensure that Americans continue to enjoy the most diverse, affordable and high quality food, fuel and fiber products in the world. It۪s is a win for the nation۪s agricultural producers, who now have the certainty to plan for the future, a win for the 16 million people working in the industry and a big win for American consumers.','national',1,'partisan',1,'policy',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3559,'facebook','John Hoeven (Senator from North Dakota)','Yesterday I questioned Continental Resources CEO Harold Hamm during a Senate Energy Committee hearing on the best ways for the federal government to help industry produce the energy necessary for our nation to become energy independent.  Watch video of our conversation https://www.youtube.com/watch?v=DxmVTSB7-I0&feature=youtu.be  http://www.hoeven.senate.gov/public/index.cfm/2014/1/hoeven-hamm-talk-energy-independence-crude-transportation-safety-energy-infrastructure','national',1,'neutral',1,'support',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3560,'facebook','John Hoeven (Senator from North Dakota)','This legislation is about fairness and affordability. It prevents FEMA from unfairly increasing flood insurance rates on North Dakotans and Americans. The legislation includes our Flood Safe Basements Act to ensure that communities are able to continue using floodproofed basement credits. We need affordable and fair flood insurance premiums. We need to get this done.  http://www.hoeven.senate.gov/public/index.cfm/2014/1/hoeven-bill-preventing-flood-insurance-rate-hikes-moves-to-senate-floor-hoeven-continues-push-for-passage','national',1,'partisan',1,'support',1,1);
