INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3562,'facebook','John Hoeven (Senator from North Dakota)','Looking forward to hosting US Interior Secretary Sally Jewell in North Dakota this week. The comprehensive, states first approach to energy development we۪ve pursued with Empower North Dakota over the past decade has helped to produce the lowest unemployment rate and the fastest growing income in America, as well as record surpluses to meet the priorities of the people of North Dakota. We۪re bringing Secretary Jewell to the state to show her that the nation can do the same to create jobs, economic opportunity and energy security and independence for the American people.','constituency',1,'neutral',1,'policy',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3563,'facebook','John Hoeven (Senator from North Dakota)','Enjoyed speaking this afternoon with Raymond Salata, Christian Anheluk, Haley Lund, and Harshita Gaba.  These talented high school students are representing North Dakota during this year۪s Boys and Girls State.','constituency',1,'neutral',1,'information',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3564,'facebook','John Hoeven (Senator from North Dakota)','I believe the administration۪s request for funds to study how to eliminate nuclear missile silos is misguided. The fact is we can achieve the force levels required under the New START agreement without reducing silos and hurting our nuclear deterrent capabilities. I will therefore oppose such funding as the Appropriations Committee considers the Fiscal Year 2014 budget for the Department of Defense.۝','national',1,'partisan',1,'policy',1,1);
