INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3576,'facebook','Denny Heck (Representative from Washington)','Great to see Puyallup\'s Denis Gray Trucking Inc. receive national recognition from the U.S. Small Business Administration as Washington\'s Family-owned Small Business of the Year. Congratulations to Denis\' son Michael for his success. http://www.thenewstribune.com/2014/04/30/3173546/puyallup-trucking-firm-denis-gray.html  As a small business owner, I know the determination it takes to stay open. More than half of Americans own or work for a small business, and we all benefit from their innovations, job creation, and ability to make the U.S. more competitive globally. I support tax credits that help South Sound small businesses hire more employees and support renewal of the Export-Import Bank to give small firms access to foreign markets.','constituency',1,'neutral',1,'support',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3577,'facebook','Denny Heck (Representative from Washington)','Exciting times at Mount Rainier National Park! Today from 3:00  4:00 p.m. (PDT), Wildlife Ecologist Mason Reid will answer questions live via Facebook on the park۪s plans to reintroduce fishers to their historical range. Here\'s the event page if you want to participate.','constituency',0.517,'neutral',1,'information',1,2);
