INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3580,'facebook','Denny Heck (Representative from Washington)','This weekend is the twentieth anniversary of the establishment of Americorps. For twenty years Americorps has been making our communities better places to live and work. The 10th Congressional District is the site of multiple Americorps programs, serving veterans and job seekers and working to preserve the natural beauty of Washington state. To all Americorps members past and present, thank you for your service to our nation. I hope many more Americans choose to follow in your footsteps in the years ahead.','national',1,'neutral',1,'personal',1,1);
