INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3590,'facebook','Richard Hudson (Representative from North Carolina)','Today marks the 41st anniversary of a tragic event in our nation۪s history  a devastating Supreme Court decision that has caused the death of nearly 56 million innocent children. As a country, we have a moral obligation to stand up to protect the most innocent in our society and to be a voice for those who are not given the opportunity to speak.   Read my statement: http://1.usa.gov/1eAyq3m','national',1,'partisan',1,'policy',1,1);
