INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3742,'facebook','Derek Kilmer (Representative from Washington)','I applaud the Senate for passing a 5 month extension on unemployment insurance for nearly 3 million Americans in a bipartisan vote, and now I am calling on the House to do the same. I have held a roundtable discussion with workers in our region and social service providers to discuss unemployment insurance and the importance of extending this program, and also signed onto a letter to Speaker Boehner requesting a vote on unemployment insurance. Extending emergency unemployment insurance is important for Washington State families. Nearly 25,000 people in our state lost this insurance at the end of December because Congress failed to provide an extension. Its time to #ExtendUI.','national',1,'neutral',1,'policy',1,1);
