INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3746,'facebook','Ann Kuster (Representative from New Hampshire)','I was devastated to learn today about the tragic death of Lance Corporal Brandon Garabrant, who served his country so bravely in Afghanistan. Through his dedicated service as a volunteer fire fighter and as a Marine, Lance Corporal Garabrant made his family, his state and his nation proud.  The thoughts and prayers of the entire Granite State are with Lance Corporal Garabrant۪s family and friends. It is our responsibility as Granite Staters and as Americans to protect and honor all those who serve our great nation and guard our freedom.','national',1,'neutral',1,'support',1,1);
