INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3955,'facebook','Tim Murphy (Representative from Pennsylvania)','The White House delayed certain Affordable Care Act rules and regulations until after the 2012 elections - but NOT for the reasons explained by WH officials, according to a new bombshell Washington Post report. Watch my exclusive Sunday interview on Fox News about this troubling revelation and to hear me talk more about my Helping Families in #MentalHealth Crisis Act to reform our broken mental health system. Watch: http://bitly.com/Js4f38','national',1,'partisan',1,'policy',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3956,'facebook','Tim Murphy (Representative from Pennsylvania)','Safe, reliable #shalegas is transforming #SWPA into the global leader in energy, manufacturing, and chemical production. There are some, however, who want to turn back the clock on this energy renaissance. I۪ll discuss why how natural gas fits into a national energy plan during a town hall meeting *today* at KDKA Radio۪s #MarcellusFest on Stage AE on the North Side. Click here for more information:','constituency',1,'neutral',1,'policy',1,1);
