INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3959,'facebook','Lisa Murkowski (Senator from Alaska)','What a win-win for Alaska! College campuses all over Alaska from Barrow to the Aleutians will receive $23.8 million in biomedical research funding to advance education of minority students to save lives and improve health care in rural Alaska.   It۪s a positive advancement to keep promising students in Alaska while also empowering them to carve out an important career path.','constituency',1,'neutral',1,'personal',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3960,'facebook','Lisa Murkowski (Senator from Alaska)','This week marks 6 years since TransCanada applied for a permit to build the Keystone XL pipeline to create thousands of jobs and boost America۪s energy industry.  My colleagues and I commemorated the occasion by saying the president needs to stop dragging his feet and allow folks to get to work.    https://www.youtube.com/watch?v=WdTbdR-36C8&feature=youtu.be#t=11m53','national',1,'partisan',1,'personal',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (3961,'facebook','Lisa Murkowski (Senator from Alaska)','Good news for Alaska\'s military community! I just got off the phone in Haines with the Air Force Secretary, who told us that Eielson Air Force Base has been named a \'preferred\' location -- in other words, the front runner -- for two squadrons of F-35 II\'s.','constituency',1,'neutral',1,'information',1,1);
