INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4230,'facebook','Ted Poe (Representative from Texas)','\"On January 10, 1901, an enormous geyser of oil exploded from a drilling site at Spindletop Hill, a mound created by an underground salt deposit located near Beaumont in Jefferson County, southeastern Texas. Reaching a height of more than 150 feet and producing close to 100,000 barrels a day, the \"gusher\" was more powerful than any previously seen in the world. A booming oil industry soon grew up around the oil field at Spindletop, and many of the major oil companies in America, including Gulf Oil, Texaco and Exxon, can trace their origins there.\"  http://www.history.com/topics/spindletop','constituency',1,'neutral',1,'policy',1,1);
