INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4304,'facebook','Mike Quigley (Representative from Illinois)','This month I\'m honoring the 1963 Loyola University Chicago Ramblers on the 50th Anniversary of their NCAA Championship. During the height of the civil rights movement, the team revolutionized collegiate basketball with their starting five \"Iron Men,\" a majority of whom were black. House Resolution 298 celebrates the team for breaking ground far beyond the hardwood of the basketball court. Read it at http://1.usa.gov/1bG0Uro.','constituency',1,'neutral',1,'personal',1,1);
