INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4628,'facebook','Kurt Schrader (Representative from Oregon)','Congressman speaks with veterans affected by toxins\n \n \n Created on Thursday, 14 November 2013 00:00. | Written by Patrick Malee. | Print \n \n by: TIDINGS PHOTO: VERN UYETAKE - U.S. Navy veterans Marlyn Miller and Daniel Larsen spoke to U.S. Congressman Kurt Schrader on Veterans Day about their exposure to toxic chemicals while in the service. \n \n When Daniel Larsen stood up to speak at a mid-October town hall meeting hosted by U.S. Congressman Kurt Schrader, it wasn۪t to voice a complaint about the recent government shutdown or to discuss second amendment rights. \n \n The issue he brought forward was very specific, one that he and scores of other Navy veterans had dealt with for years, yet remained unknown to officials like Schrader. \n \n http://portlandtribune.com/wlt/95-news/201047-congressman-speaks-with-veterans-affected-by-toxins','national',1,'neutral',1,'personal',1,1);
