INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4779,'facebook','Niki Tsongas (Representative from Massachusetts)','In 1964, President Johnson declared a war on poverty. 50 years later, 46.5 million Americans  and 13 percent of residents in the 3rd district of MA  live below the poverty line. There is still work to be done. America thrives on the principle that if you work hard, you can support your family. Let us recommit ourselves to ensuring that every citizen has the tools and opportunities to succeed in this country.','national',1,'neutral',1,'policy',1,1);
