INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4830,'facebook','Marc Veasey (Representative from Texas)','Please help me welcome a new addition to our team! Meet Chinwe, the newest intern in our Washington DC office. Chinwe is from Dallas, TX and a graduate of Texas Tech University with a double major in Technical Communication and Public Relations. She is currently pursuing a Master۪s Degree in Technical Communication and Rhetoric at Texas Tech as well. She enjoys playing flag football and volleyball, running, and is a foodie.   Chinwe has literally hit the ground running this week, assisting in our DC Health and Fitness Expo and joining the Veasey team at the ACLI Capital Challenge for a 3-mile run. Thanks for your hard work this week Chinwe!','national',1,'partisan',1,'support',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4831,'facebook','Marc Veasey (Representative from Texas)','Tonight, I will discuss Voter ID laws live on MSNBC۪s The Rachel Maddow Show at 9:10 p.m. ET/8:10 p.m. CT. The Rachel Maddow Show, airs on MSNBC at 9:00 p.m. Eastern, Monday through Friday, and is rebroadcast at midnight Eastern. Please check your local listings for exact times and station details in your area. Tune in! http://www.msnbc.com/rachel-maddow-show','national',1,'neutral',1,'personal',1,1);
