INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4840,'facebook','Maxine Waters (Representative from California)','I was proud to join with my colleagues today to announce the creation of the Congressional Full Employment Caucus, to serve as a platform and working group for Members of Congress dedicated to identifying solutions to our current unemployment crisis and advocating for legislative action. As Congressional leaders, we must focus on long-term strategies that put Americans back to work and eliminate the wage gap.','national',1,'neutral',1,'policy',1,1);
