INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4842,'facebook','Maxine Waters (Representative from California)','President John F. Kennedy signed the Equal Pay Act into law 50 years ago today. Unfortunately, our fight for equal pay for equal work is not yet complete. Women still earn just 77 cents for every dollar earned by men. On this important anniversary, let us all recommit to work together until we have achieved an America where women are truly paid equal pay for equal work.','national',1,'partisan',1,'personal',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4843,'facebook','Maxine Waters (Representative from California)','Today marks the 13th annual National Black HIV/AIDS Awareness Day. According to the CDC, African-Americans make up just 14 percent of the U.S. population, but account for almost half of all people living with HIV. I will continue my efforts to promote HIV/AIDS awareness, prevention, testing and treatment for all Americans. There is more work to be done, and each of us must do all that we can to eradicate HIV/AIDS worldwide.','national',1,'neutral',1,'information',1,1);
