INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4855,'facebook','Greg Walden (Representative from Oregon)','Did you know the President is able to designate land as a national monument with just the stroke of his pen? Last week, with my strong support, the House took action requiring public input for future sites of national monuments.   Here۪s an editorial from The Bulletin #inBend about this common-sense bill. http://www.bendbulletin.com/opinion/editorials/1931357-151/editorial-monuments-bill-makes-common-sense-changes','national',1,'neutral',1,'support',1,1);
