INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4895,'facebook','Mark Warner (Senator from Virginia)','It\'s good to see Congress return to an orderly process. This two-year agreement should help Virginia families, employers and community leaders move forward with more predictability and confidence. While I۪m pleased that, at least in the near term, we will not be lurching from crisis to crisis, we still need to have an adult conversation about our nation۪s spending and debt.','constituency',1,'partisan',1,'support',1,1);
