INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4936,'facebook','Ann Wagner (Representative from Missouri)','Today I am excited to celebrate Small Business Saturday - a day to honor hardworking entrepreneurs that are the backbone of our economy.\n \n If you are a small business owner or know someone who is, please email me at WagnerBusiness@mail.house.gov with the owner۪s name, a picture of the business, and a few sentences about what the business does. At the end of next week, I will recognize the entrants with an official Congressional Entrepreneur Certificate.۝ I will also post a list of them on my Facebook page, so sign up today and be recognized!\n \n Small businesses truly are the engine for job creation, and if you have a moment please show your appreciation by shopping at one today.','national',1,'neutral',1,'mobilization',1,1);
