INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4971,'facebook','Don Young (Representative from Alaska)','Today, the Senate unanimously approved legislation I introduced and passed in the House that will expand the Alaska Native Tribal Health Consortium۪s (ANTHC) ability to provide health services to patients, particularly for those living in rural areas and villages not connected by road. This legislation, which has been sent to the President to be signed into law, will transfer a 2.79 acre parcel of federal land to ANTHC, where they plan to build a 170-room housing facility which will adjoin the Alaska Native Medical Center in Anchorage.','constituency',1,'neutral',1,'policy',1,1);
