INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4977,'facebook','Kevin Yoder (Representative from Kansas)','Listening to veterans in the 3rd District is very important to me. Today at VFW Post #846 in Overland Park, I heard from my Veterans Advisory Committee about the issues they are facing with the VA and we discussed the Veteran\'s Access, Choice and Accountability Act of 2014 that I supported and was recently signed into law. This new law represents major reforms to improve access to quality care for our veterans. A special thank you to these vets for their service to our country and for informing me about their issues with the VA.','constituency',1,'neutral',1,'policy',1,1);
