INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4979,'facebook','Kevin Yoder (Representative from Kansas)','Our 4th Annual 3rd District Job Fair is only ten days away. If you or someone you know is looking for employment, then please spread the word and join us on Thursday, August 14 at the Great Mall of the Great Plains. The Job Fair will last from 9:00 a.m. until 1:00 p.m., with the 9:00-10:00 a.m. hour exclusively for veterans. Job seekers should dress for success, bring several copies of their resume, and do not need to register or pay to attend. For more information, visit www.Yoder.house.gov, or please contact my Overland Park District Office at (913) 621-0832. #YoderJobFair   http://youtu.be/rTP0GJBEt-w?list=UUCeYmn4A8kZEHCcAfeUW9lQ','national',1,'neutral',1,'personal',1,1);
INSERT INTO `rep_docs` (`id`,`media_source`,`author`,`text_content`,`audience`,`audience_conf`,`bias`,`bias_conf`,`message`,`message_conf`,`trusted_judgments`) VALUES (4980,'facebook','Kevin Yoder (Representative from Kansas)','I had the opportunity to meet with British Member of Parliament Andrew Bridgen from North West Leicestershire today. He is visiting Washington, DC with other MPs to shadow Members of Congress for the afternoon to observe various meetings and see the House debate and vote. I enjoyed meeting with MP Bridgen and discuss the similarities and differences between our two bodies of government.','national',1,'neutral',1,'personal',1,1);
